import{r as o,o as s,c as a,a as i,b as n,w as p,F as l,e as r,d as e}from"./app.a072516b.js";import{_ as c}from"./plugin-vue_export-helper.21dcd24c.js";const d={},g=r('<h1 id="inviting-and-giving-people-roles" tabindex="-1"><a class="header-anchor" href="#inviting-and-giving-people-roles" aria-hidden="true">#</a> Inviting And Giving People Roles</h1><p>First, go to the website and login, you will appear on the main screen like this.</p><p><img src="https://2159974755-files.gitbook.io/~/files/v0/b/gitbook-x-prod.appspot.com/o/spaces%2FunMTB348tgzMbMDFEblw%2Fuploads%2F0sjzGukZYvQQXX0pidzl%2FScreenshot 2022-01-11 213936.png?alt=media&amp;token=1d1914fb-0979-4b3c-96e8-84edcf42d79a" alt=""></p><p>Second, go to the bar on the left side of the screen and press settings.</p><p><img src="https://2159974755-files.gitbook.io/~/files/v0/b/gitbook-x-prod.appspot.com/o/spaces%2FunMTB348tgzMbMDFEblw%2Fuploads%2FdAr6HGQJ5lNW7qGhJdpW%2FScreenshot 2022-01-11 214949.png?alt=media&amp;token=6ee703ac-0a63-44f3-a49c-71374edc0805" alt=""></p><p>Press the invite tab.</p><p><img src="https://2159974755-files.gitbook.io/~/files/v0/b/gitbook-x-prod.appspot.com/o/spaces%2FunMTB348tgzMbMDFEblw%2Fuploads%2FIJEa4VBJ8BF826Cw7VK9%2FScreenshot 2022-01-12 174517.png?alt=media&amp;token=73b8b6c6-35a1-4b25-831c-33c75b5461b5" alt=""></p><p>You can create an invite by pressing &quot;new invite&quot; and when the invite it pressed the user will be given the role that you assign. You can change the assigned role by pressing the arrow on the invite.</p><p><img src="https://2159974755-files.gitbook.io/~/files/v0/b/gitbook-x-prod.appspot.com/o/spaces%2FunMTB348tgzMbMDFEblw%2Fuploads%2FtQ3j9NIdueY8QoQ3sPVS%2FScreenshot 2022-01-12 180714.png?alt=media&amp;token=4b441338-d36b-493e-b33c-3defb118b62a" alt=""></p><p><img src="https://2159974755-files.gitbook.io/~/files/v0/b/gitbook-x-prod.appspot.com/o/spaces%2FunMTB348tgzMbMDFEblw%2Fuploads%2FTWDqSooeDMmVIQFCYm3j%2FScreenshot 2022-01-12 180756.png?alt=media&amp;token=ed047ae4-ff57-4094-bd96-fc387a072db7" alt=""></p><p>You can view all the users by selecting &quot;users&quot; in the settings this will show all the users that have accepted the invite. You can change the role of the person by pressing the arrow on the name and changing it shown in the second picture.</p><p><img src="https://2159974755-files.gitbook.io/~/files/v0/b/gitbook-x-prod.appspot.com/o/spaces%2FunMTB348tgzMbMDFEblw%2Fuploads%2F9Wkakh8gzpqdy4qfOppY%2FScreenshot 2022-01-12 181309.png?alt=media&amp;token=c5f4937b-96ac-4b10-a7ad-f3eff5222376" alt=""><img src="https://2159974755-files.gitbook.io/~/files/v0/b/gitbook-x-prod.appspot.com/o/spaces%2FunMTB348tgzMbMDFEblw%2Fuploads%2FyDJSQFxC7x1zVWgZiAhT%2FScreenshot 2022-01-12 181724.png?alt=media&amp;token=d13a9b58-264e-40c4-b899-4da91cdf6377" alt=""></p>',12),b=e("Read more about roles and making roles in "),h=e("roles.md");function m(u,F){const t=o("RouterLink");return s(),a(l,null,[g,i("p",null,[b,n(t,{to:"/guide/permissions/roles.html",title:"mention"},{default:p(()=>[h]),_:1})])],64)}var v=c(d,[["render",m]]);export{v as default};
